mapper.select:
select id, memberNo, password, phone, memberName, memberType, sex, idcardNo, address, expireDate, company, status, createTime, period, lastLogin from H_MEMBER where 1=1
and status = #{pd.status}
and memberType = #{pd.memberType}
and phone = #{pd.phone}
and memberName LIKE CONCAT(CONCAT('%', #{pd.memberName}),'%')
and memberNo = #{pd.memberNo}
and idcardNo = #{pd.idcardNo}

mapper.update:
update H_member set 
lastLogin = #{lastLogin} 
where id = #{id}

update H_member set 
password = #{password} 
where id = #{id}

update H_member set 
phone = #{phone} 
where id = #{id}
